import "./View.css";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { supabase } from "../client";

const View = (props) => {
  const [posts, setPosts] = useState([]);
  let currentPost = [];
  const {id} = useParams();

  useEffect(() => {
    const fetchPosts = async () => {
      const { data } = await supabase.from("Game").select();

      setPosts(data);
    };
    fetchPosts();
  }, [props]);

  const setUp = () =>{
    if(posts)
      currentPost = posts.find((i) => (i.id = id));
  }
  console.log(currentPost);

  return (
    <div>
      {setUp()}
      {posts &&
        <div><h1>Say Hi to {currentPost.name}</h1>
      <p>Speed: {currentPost.speed}</p>
      <p>Color: {currentPost.color}</p>
      </div>
      }

    </div>
  );
};

export default View;
