import React from 'react';
import { useState } from 'react';
import { supabase } from '../client';
import './CreatePost.css'

const CreatePost = () => {
    const [post, setPost] = useState({name: "", speed: "", color: ""})

    const handleChange = (event) => {
      const { n, value } = event.target;

      if (n === "name" || n === "speed" || name === "color") {
        setPost((prev) => ({
          ...prev,
          [name]: value,
        }));
      }
    };


    const createPost = async (event) => {
      event.preventDefault();

      console.log("Post data:", post);
      await supabase
        .from("Game")
        .insert({
          name: post.name,
          speed: post.speed,
          color: post.color,
        })
        .select();

      // window.location = "/read";
    };

    return (
      <div>
        <form>
          <label htmlFor="name">Name</label> <br />
          <input type="text" id="name" name="name" onChange={handleChange} />

          <label htmlFor="speed">Speed</label>
          <input type="text" id="speed" name="speed" onChange={handleChange} />

          <label htmlFor="color">Color</label>
          <input type="text" id="color" name="color" onChange={handleChange} />

          <input type="submit" value="Submit" onClick={createPost} />
        </form>
      </div>
    );
}

export default CreatePost
